CREATE TABLE IF NOT EXISTS `ci_sessions` (
  `session_id` varchar(40) collate utf8_unicode_ci NOT NULL default '0',
  `ip_address` varchar(16) collate utf8_unicode_ci NOT NULL default '0',
  `user_agent` varchar(120) collate utf8_unicode_ci NOT NULL,
  `last_activity` int(10) unsigned NOT NULL default '0',
  `user_data` text collate utf8_unicode_ci NOT NULL,
  PRIMARY KEY  (`session_id`),
  KEY `last_activity_idx` (`last_activity`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

CREATE TABLE IF NOT EXISTS `footer_links` (
  `footer_id` int(11) NOT NULL auto_increment,
  `footer_text` varchar(100) collate utf8_unicode_ci NOT NULL,
  `footer_link` varchar(60) collate utf8_unicode_ci NOT NULL,
  PRIMARY KEY  (`footer_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=6 ;


CREATE TABLE IF NOT EXISTS `menus` (
  `menu_id` bigint(20) NOT NULL auto_increment,
  `menu_title` varchar(50) collate utf8_unicode_ci NOT NULL,
  `menu_number` int(11) NOT NULL,
  `menu_link` varchar(60) collate utf8_unicode_ci NOT NULL,
  PRIMARY KEY  (`menu_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=17 ;


CREATE TABLE IF NOT EXISTS `pages` (
  `page_id` bigint(20) NOT NULL auto_increment,
  `page_num` int(11) NOT NULL,
  `page_content` text collate utf8_unicode_ci,
  `site_id` bigint(20) NOT NULL,
  `title` varchar(50) collate utf8_unicode_ci NOT NULL,
  `page_type` smallint(6) NOT NULL,
  PRIMARY KEY  (`page_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=48 ;


CREATE TABLE IF NOT EXISTS `sites` (
  `site_id` bigint(20) NOT NULL auto_increment,
  `site_name` varchar(50) collate utf8_unicode_ci NOT NULL,
  `user_id` bigint(20) default NULL,
  `template_id` bigint(20) NOT NULL,
  `domain` varchar(63) collate utf8_unicode_ci default NULL,
  PRIMARY KEY  (`site_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=31 ;



CREATE TABLE IF NOT EXISTS `users` (
  `id` bigint(20) NOT NULL auto_increment,
  `email` varchar(60) collate utf8_unicode_ci NOT NULL,
  `password` char(32) collate utf8_unicode_ci NOT NULL,
  `domain` varchar(63) collate utf8_unicode_ci NOT NULL,
  `priority` int(11) NOT NULL default '1',
  PRIMARY KEY  (`id`),
  UNIQUE KEY `email` (`email`),
  UNIQUE KEY `domain` (`domain`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=31 ;

